mapper.update:
UPDATE transaction
PAYMENT_OPTION = #{paymentOption}, RECEIVER_NAME = #{receiverName}, RECEIVER_PHONE = #{receiverPhone}, DEMAILADDR = #{divyAddr}, DLVY_REQUEST = #{divyRequest}, DLVY_DATE = SYSDATE
WHERE TRAN_NO=#{tranNo}

mapper.select:
  	SELECT COUNT(*)
  	FROM(	SELECT tran_no 
					FROM transaction
					where buyer_id = #{buyerId}
					) countTable
